{\\r\\n                        \\\"key\\\": \\\"death\\\",\\r\\n                        \\\"label\\\": \\\"threaten the spider with your sharp spear.\\\",\\r\\n                        \\\"requirements\\\": {\\r\\n                            \\\"might\\\": 2\\r\\n                        },\\r\\n                        \\\"rejection\\\": \\\"The spider sees no danger in your puny weapon.\\\"\\r\\n                    },\\r\\n                    {\\r\\n                        \\\"key\\\": \\\"prayer\\\",\\r\\n                        \\\"label\\\": \\\"hypnotize the spider to learn a secret.\\\",\\r\\n                        \\\"requirements\\\": {\\r\\n                            \\\"prayer\\\": 1\\r\\n                        },\\r\\n                        \\\"rejection\\\": \\\"Your chanting fails to mesmerize the spider.\\\"\\r\\n                    }\\r\\n                ]\\r\\n            },\\r\\n            \\\"reputation\\\": {\\r\\n                \\\"description\\\": \\\"the spider is convinced that you are worthy of his treasure.\\\",\\r\\n                \\\"options\\\": [],\\r\\n                \\\"characteristics\\\": {\\r\\n                    \\\"reputation\\\": 1\\r\\n                }\\r\\n            },\\r\\n            \\\"death\\\": {\\r\\n                \\\"description\\\": \\\"the spider is enraged by your insolent threat. with an invisible leap, he appears right before you and impales you with his sharp teeth. you die.\\\",\\r\\n                \\\"options\\\": [],\\r\\n                \\\"characteristics\\\": {\\r\\n                    \\\"might\\\": -1\\r\\n                }\\r\\n            },\\r\\n            \\\"prayer\\\": {\\r\\n                \\\"description\\\": \\\"you learnt a secret, but you wished you didn\'t.\\\",\\r\\n                \\\"options\\\": [],\\r\\n                \\\"characteristics\\\": {\\r\\n                    \\\"prayer\\\": -1\\r\\n                }\\r\\n            },\\r\\n            \\\"finalOutcome\\\": {\\r\